mapper.select:
select trim(h.dealAmount)
from housedeal h join houseinfo i
using (aptCode)
where i.aptName = #{aptname}
order by h.dealAmount desc
limit 1;

select trim(h.dealAmount)
from housedeal h join houseinfo i
using (aptCode)
where i.aptName = #{aptname}
order by h.dealAmount
limit 1;

mapper.update:
update houseinfo
set hit = hit + 1
where aptCode = #{aptCode}
